import React, { Component } from 'react';
import Bullet from "./Bullet.js";
import Note from "./Note.js";
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      notes: this.props.store.getState().notes,
      currentNotePosition: this.props.store.getState().currentNotePosition
    }
  }

  componentDidMount() {
    /*
    const { notes, currentNotePosition } = this.props.store.getState();
    this.setState({ notes, currentNotePosition });
    this.unsubscribe = this.props.store.subscribe(() => {
      const { notes, currentNotePosition } = this.props.store.getState();
      this.setState({ notes, currentNotePosition });
    });
    */
  }

  render() {
    let currentNote = this.state.notes[this.state.currentNotePosition[0]];
    console.log(currentNote);
    return (
      <div className="container">
        <h1 className="display-1">{ currentNote.content }</h1>
        <h1 className="display-5">{ currentNote.note }</h1>
        <hr></hr>
        { currentNote.children.length > 0 &&
            currentNote.children.map(child =>
              <Bullet note={ child }></Bullet>
            )
        }
      </div>
    );
  }
}

export default App;
