const ADD_BULLET = "ADD_BULLET";
const EDIT_BULLET = "EDIT_BULLET";
const REMOVE_BULLET = "REMOVE_BULLET";
const INDENT_BULLET = "INDENT_BULLET";
const UNINDENT_BULLET = "UNINDENT_BULLET";
const CHANGE_CURRENT_NOTE = "CHANGE_CURRENT_NOTE";

function addBullet(position) {
  return {
    type: ADD_BULLET,
    position
  }
}

function editBullet(position, content) {
  return {
    type: EDIT_BULLET,
    position,
    content
  }
}

function removeBullet(position) {
  return {
    type: REMOVE_BULLET,
    position
  }
}

function indentBullet(position) {
  return {
    type: INDENT_BULLET,
    position
  }
}

function unindentBullet(position) {
  return {
    type: UNINDENT_BULLET,
    position
  }
}

function getNote(positionArray, notes) {
  let index = positionArray.shift();
  if (positionArray.length == 0) {
    return notes[index]
  }
  else {
    return getNote(positionArray, notes[index].children)
  }
}

function addNote(positionArray, store) {
  console.log(positionArray);
  let currentNotePosition = positionArray.pop();
  let nextParent = positionArray.shift();
  let parentNote = store.notes[nextParent].children;
  while (positionArray.length > 0) {
    nextParent = positionArray.shift();
    parentNote = parentNote[nextParent].children;
  }
  parentNote.splice(currentNotePosition, 0, {content: "", note: "", completed: false, children: []});
  return store;
}

export { ADD_BULLET,
         EDIT_BULLET,
         REMOVE_BULLET,
         INDENT_BULLET,
         UNINDENT_BULLET,
         addBullet,
         editBullet,
         removeBullet,
         indentBullet,
         unindentBullet,
         getNote,
         addNote };
