const ADD_BULLET = "ADD_BULLET";
const EDIT_BULLET = "EDIT_BULLET";
const REMOVE_BULLET = "REMOVE_BULLET";
const INDENT_BULLET = "INDENT_BULLET";
const UNINDENT_BULLET = "UNINDENT_BULLET";

function addBullet(position) {
  return {
    type: ADD_BULLET,
    position
  }
}

function editBullet(position, content) {
  return {
    type: EDIT_BULLET,
    position,
    content
  }
}

function removeBullet(position) {
  return {
    type: REMOVE_BULLET,
    position
  }
}

function indentBullet(position) {
  return {
    type: INDENT_BULLET,
    position
  }
}

function unindentBullet(position) {
  return {
    type: UNINDENT_BULLET,
    position
  }
}

function getNote(positionArray, notes) {
  index = positionArray.shift();
  if (positionArray.length == 0) {
    return notes[index]
  }
  else {
    return getNote(positionArray, notes.children[index])
  }
}

export { ADD_BULLET,
         EDIT_BULLET,
         REMOVE_BULLET,
         INDENT_BULLET,
         UNINDENT_BULLET,
         addBullet,
         editBullet,
         removeBullet,
         indentBullet,
         unindentBullet };
