import $ from "jquery";

const ADD_BULLET = "ADD_BULLET";
const EDIT_BULLET = "EDIT_BULLET";
const REMOVE_BULLET = "REMOVE_BULLET";
const INDENT_BULLET = "INDENT_BULLET";
const UNINDENT_BULLET = "UNINDENT_BULLET";
const CHANGE_CURRENT_NOTE = "CHANGE_CURRENT_NOTE";

function addBullet(position, uuid) {
  return {
    type: ADD_BULLET,
    position,
    uuid,
    exec: (state) => {
      let childPosition = position.pop();
      let parent = state.root[position.shift()].children;
      while (position.length > 0) {
        parent = state.root[position.shift()].children;
      }
      parent.splice(childPosition + 1, 0, {content: "", note: "", id: uuid, completed: false, children: []});
      setTimeout(() => {
        document.getElementById(uuid).focus();
      }, 0);
      return state;
    }
  }
}

function editBullet(position, content) {
  return {
    type: EDIT_BULLET,
    position,
    content
  }
}

function removeBullet(position) {
  return {
    type: REMOVE_BULLET,
    position
  }
}

function indentBullet(position) {
  return {
    type: INDENT_BULLET,
    position,
    exec: (state) => {
      let childPosition = position.slice(-1)[0];
      if (childPosition == 0) { return state }
      let tree = getTree(position.slice(0, -1), state.root);
      let newParent = getNode(position.splice(-1, 1, (childPosition - 1)));
      let oldParent = tree.slice(-1)[0];
      let child = oldParent.children.splice(childPosition, 1)[0];
      newParent.children.push(child);
      tree.push(newParent);
      setTimeout(() => {
        tree.map(parent => {
          $(`#${parent.id}`).closest(".bullet").children(".collapse").addClass("show");
        });
      }, 0);
      return state;
    }
  }
}

function unindentBullet(position) {
  return {
    type: UNINDENT_BULLET,
    position,
    exec: (state) => {
      let childPosition = position.slice(-1)[0];
      let oldParentPosition = position.slice(-2)[0];
      if (childPosition == 0) { return state }
      let tree = getTree(position.slice(0, -1), state.root);
      let newParent = tree.slice(-2,-1)[0];
      let oldParent = tree.slice(-1)[0];
      let child = oldParent.children.splice(childPosition, 1)[0];
      newParent.children.splice((oldParentPosition + 1), 0, child);
      setTimeout(() => {
        tree.slice(0,-2).map(parent => {
          $(`#${parent.id}`).closest(".bullet").children(".collapse").addClass("show");
        });
      }, 0);
      return state;
    }
  }
}

function getNode(indices, root) {
  let node = root[indices.shift()];
  while (indices.length > 0) {
    node = node.children[indices.shift()];
  }
  return node;
}

function getTree(indices, root) {
  let tree = [];
  let node = root[indices.shift()];
  tree.push(node)
  while (indices.length > 0) {
    node = node.children[indices.shift()];
    console.log("node", node);
    tree.push(node);
  }
  return tree;
}



export { ADD_BULLET,
         EDIT_BULLET,
         REMOVE_BULLET,
         INDENT_BULLET,
         UNINDENT_BULLET,
         addBullet,
         editBullet,
         removeBullet,
         indentBullet,
         unindentBullet,
         getNode,
         getTree };
