import $ from "jquery";

const UPDATE_ROOT = "UPDATE_ROOT";
const ADD_BULLET = "ADD_BULLET";
const EDIT_BULLET = "EDIT_BULLET";
const DELETE_BULLET = "DELETE_BULLET";
const INDENT_BULLET = "INDENT_BULLET";
const UNINDENT_BULLET = "UNINDENT_BULLET";
const MOVE_BULLET = "MOVE_BULLET";
const GO_DOWN = "GO_DOWN";
const GO_UP = "GO_UP";

function updateRoot(newRoot) {
  return {
    type: UPDATE_ROOT,
    newRoot,
    exec: (state) => {
      state.root = newRoot;
      return state;
    }
  }
}

function addBullet(position, uuid) {
  return {
    type: ADD_BULLET,
    position,
    uuid,
    exec: (state) => {
      let childPosition = last(position);
      let parent = getNode(ancestors(position), state.root);
      parent.children.splice(childPosition+1, 0, {content: "", note: "", id: uuid, completed: false, children: []});
      focusNode({id: uuid});
      return state;
    }
  }
}

function editBullet(position, content) {
  return {
    type: EDIT_BULLET,
    position,
    content,
    exec: (state) => {
      let node = getNode(position, state.root);
      node.content = content;
      return state;
    }
  }
}

function deleteBullet(position) {
  return {
    type: DELETE_BULLET,
    position,
    exec: (state) => {
      let parent = getNode(ancestors(position), state.root);
      let node = parent.children.splice(last(position), 1)[0];
      if (node.children.length > 0) { return state }
      if (last(position) > 0) {
        let siblingAbove = parent.children.slice(last(position)-1)[0];
        focusNode(siblingAbove);
      }
      else {
        focusNode(parent);
      }
      return state;
    }
  }
}

function indentBullet(position) {
  return {
    type: INDENT_BULLET,
    position,
    exec: (state) => {
      if (last(position) === 0) { return state }
      let tree = getTree(ancestors(position), state.root);
      let oldParent = last(tree);
      let newParent = getNode([...ancestors(position), (last(position)-1)], state.root);
      let child = oldParent.children.splice(last(position),1)[0];
      newParent.children.push(child);
      tree.push(newParent);
      uncollapseTree(tree);
      focusNode(child);
      return state;
    }
  }
}

function unindentBullet(position) {
  return {
    type: UNINDENT_BULLET,
    position,
    exec: (state) => {
      if (position.length < 3) { return state }
      let childPosition = last(position);
      let oldParentPosition = position.slice(-2)[0];
      let tree = getTree(position.slice(0, -2), state.root);
      let newParent = tree.slice(-1)[0];
      let oldParent = getNode(position.slice(0, -1), state.root);
      let child = oldParent.children.splice(childPosition, 1)[0];
      newParent.children.splice((oldParentPosition + 1), 0, child);
      setTimeout(() => {
        tree.slice(0,-2).map(parent => {
          $(`#${parent.id}`).children(".collapse").addClass("show");
          placeCaretAtEnd($(`#${child.id}`).find(".bullet-content").get(0));
          $(`#${child.id}`).find(".bullet-content").focus();
        });
      }, 0);
      return state;
    }
  }
}

function moveBulletAsChild(childPosition, newParentPosition) {
  return {
    type: MOVE_BULLET,
    childPosition,
    newParentPosition,
    exec: (state) => {
      let newParent = getNode(newParentPosition.slice(), state.root);
      let oldParent = getNode(childPosition.slice(0,-1), state.root);
      let child = oldParent.children.splice(last(childPosition), 1)[0];
      newParent.children.push(child);
      if ((childPosition.length === newParentPosition.length) &&
          (last(newParentPosition) > last(childPosition))) {
        uncollapseTree(getTree([...ancestors(newParentPosition), last(newParentPosition)-1], state.root));
        return state;
      }
      uncollapseTree(getTree(newParentPosition.slice(), state.root));
      return state;
    }
  }
}

function moveBulletAsSibling(childPosition, newSiblingPosition) {
  return {
    type: MOVE_BULLET,
    childPosition,
    newSiblingPosition,
    exec: (state) => {
      let newParent = getNode(newSiblingPosition.slice(0, -1), state.root);
      let oldParent = getNode(childPosition.slice(0,-1), state.root);
      let child = oldParent.children.splice(last(childPosition), 1)[0];
      newParent.children.splice(last(newSiblingPosition)+1, 0, child);
      return state;
    }
  }
}

function goDown(position) {
  return {
    type: GO_DOWN,
    position,
    exec: (state) => {
      let tree = getTree(position.slice(), state.root);
      let _position = position.slice();
      let childPosition = _position.pop();
      let parent = getNode(_position.slice(), state.root);
      while(childPosition === parent.children.length-1) {
        if (_position.length === 1) { return state }
        let childPosition = _position.pop();
        let parent = getNode(last(_position).slice(), state.root);
      }
      focusNode(getNode([..._position, childPosition+1], state.root));
      return state;
    }
  }
}

function goUp(position) {
  return {
    type: GO_UP,
    position,
    exec: (state) => {
      let tree = getTree(position.slice(), state.root);
      let _position = position.slice();
      let childPosition = _position.pop();
      let parent = getNode(_position.slice(), state.root);
      while(childPosition === 0) {
        if (_position.length === 1) { return state }
        let childPosition = _position.pop();
        let parent = getNode(last(_position).slice(), state.root);
      }
      focusNode(getNode([..._position, childPosition-1], state.root));
      return state;
    }
  }
}

function getNode(indices, root) {
  let node = root[indices.shift()];
  while (indices.length > 0) {
    node = node.children[indices.shift()];
  }
  return node;
}

function getTree(indices, root) {
  let tree = [];
  let node = root[indices.shift()];
  tree.push(node)
  while (indices.length > 0) {
    node = node.children[indices.shift()];
    tree.push(node);
  }
  return tree;
}

function placeCaretAtEnd(el) {
  // https://stackoverflow.com/questions/4233265/contenteditable-set-caret-at-the-end-of-the-text-cross-browser/4238971
  // @Tim Down
  el.focus();
  if (typeof window.getSelection != "undefined"
          && typeof document.createRange != "undefined") {
      var range = document.createRange();
      range.selectNodeContents(el);
      range.collapse(false);
      var sel = window.getSelection();
      sel.removeAllRanges();
      sel.addRange(range);
  } else if (typeof document.body.createTextRange != "undefined") {
      var textRange = document.body.createTextRange();
      textRange.moveToElementText(el);
      textRange.collapse(false);
      textRange.select();
  }
}

function focusNode(node) {
  setTimeout(() => {
    placeCaretAtEnd($(`#${node.id}`).find(".bullet-content").get(0));
    $(`#${node.id}`).find(".bullet-content").focus();
  }, 0);
}

function uncollapseNode(node) {
  setTimeout(() => {
    $(`#${node.id}`).children(".collapse").addClass("show");
  }, 0);
}

function uncollapseTree(tree) {
  setTimeout(() => {
    tree.map(node => {
    $(`#${node.id}`).children(".collapse").addClass("show");
    })
  }, 0);
}

function last(array) {
  return array.slice(-1)[0];
}

function ancestors(array) {
  return array.slice(0, -1);
}



export { UPDATE_ROOT,
         ADD_BULLET,
         EDIT_BULLET,
         DELETE_BULLET,
         INDENT_BULLET,
         UNINDENT_BULLET,
         MOVE_BULLET,
         GO_DOWN,
         GO_UP,
         updateRoot,
         addBullet,
         editBullet,
         deleteBullet,
         indentBullet,
         unindentBullet,
         moveBulletAsChild,
         moveBulletAsSibling,
         getNode,
         getTree,
         goUp,
         goDown };
