import React, { Component } from 'react';
import Bullet from "./Bullet.js";
import Note from "./Note.js";
import './App.css';
import { createStore } from "redux";
import yaml from "yaml-js";
import Reducer from "./Reducer.js";

let content = `
- content: ""
  note: ""
  completed: false
  children:
  - content: "Note1"
    note: ""
    completed: false
    children: []
  - content: "Note2"
    note: "Note2 Note"
    completed: true
    children:
      - content: "Note2a"
        note: ""
        completed: false
        children:
        - content: "Note2a!"
          note: ""
          completed: false
          children: []
        - content: "Note2a@"
          note: ""
          completed: false
          children: []
      - content: "Note2b"
        note: ""
        completed: false
        children: []
`;

let json = yaml.load(content);

let State = {
  notes: json,
  currentNotePosition: [0,]
}


const store = createStore(Reducer, state);

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      notes: store.getState().notes,
      currentNotePosition: store.getState().currentNotePosition
    }
  }

  componentDidMount() {
    /*
    const { notes, currentNotePosition } = this.props.store.getState();
    this.setState({ notes, currentNotePosition });
    this.unsubscribe = this.props.store.subscribe(() => {
      const { notes, currentNotePosition } = this.props.store.getState();
      this.setState({ notes, currentNotePosition });
    });
    */
  }

  render() {
    let currentNote = this.state.notes[this.state.currentNotePosition[0]];
    console.log(currentNote);
    return (
      <div className="container">
        <h1 className="display-1">{ currentNote.content }</h1>
        <h1 className="display-5">{ currentNote.note }</h1>
        <hr></hr>
        { currentNote.children.length > 0 &&
            currentNote.children.map(child =>
              <Bullet note={ child }></Bullet>
            )
        }
      </div>
    );
  }
}

export default App;
