import React, { Component } from 'react';
import ComponentManager from 'sn-components-api';
import Bullet from "./Bullet.js";
import "./App.css";
import uuidv1 from "uuid/v1";
import { createStore } from "redux";
import yaml from "yaml-js";
import Reducer from "./Reducer.js";
import * as Actions from "./Actions.js";

class App extends Component {

  constructor(props) {
    super(props);
    this.store = createStore(Reducer, { root:[{id:uuidv1(), content:"", completed:false, children:[{id:uuidv1(), content:"", completed:false, children:[]}]},], active: [] });
    this.state = this.store.getState();
  }

  componentDidMount() {
    let permissions = [{ name: "stream-context-item" }];
    this.componentManager = new ComponentManager(permissions, () => {});
    this.componentManager.streamContextItem((item) => {
      this.note = item;
  	});
    if (this.note) {
      let note = this.note;
      let root = null;
      if (note.content.text == "") {
        root = [{
          id: uuidv1(),
          content: "",
          completed: false,
          children: [ { id: uuidv1(), content: "", completed: false, children: [] }, ]
        }];
      }
      else {
        root = yaml.load(note.content.text);
        if (typeof root == "string") {
          root = [{
            id: uuidv1(),
            content: "",
            completed: false,
            children: [ { id: uuidv1(), content: root, completed: false, children: [] }, ]
          }];
        }
      }
      this.store.dispatch(Actions.updateRoot(root));
    }
    this.unsubscribe = this.store.subscribe(() => {
      let newState = this.store.getState();
      this.setState(newState);
      let { root } = newState;
      let content = yaml.dump(root);
      if (this.note) {
        let note = this.note;
        this.componentManager.saveItemWithPresave(note, () => {
          note.content.text = content;
          note.content.preview_plain = "";
          note.content.preview_html = null;
        });
      }
    });
  }

  render() {
    const { root } = this.state;
    const { active } = this.state;
    const activeBullet = Actions.getNode([0, ...active], root);
    return (
      <div className="sn-component container pt-5 ml-n4" id="bullet-editor">
        {activeBullet.content.length > 0 &&
          <div className="w-100 h-100">
            <h1 className="display-1">
              {activeBullet.content}
            </h1>
            <h1 className="display-5">
              {activeBullet.note}
            </h1>
            <hr></hr>
          </div>
        }
        <div className="editor-container" id="editor">
        { activeBullet.children.length > 0 &&
            activeBullet.children.map((child, index) =>
              <Bullet key={ child.id } position={ [...active, index] } store={ this.store } self={ child }></Bullet>
            )
        }
        </div>
      </div>
    );
  }
}

export default App;
