import React, { Component } from "react";
import { Collapse } from "react-bootstrap";
import * as Actions from "./Actions.js";
import $ from "jquery";
import uuidv1 from "uuid/v1";

class Bullet extends Component {

  constructor(props) {
    super(props);

    this.content = React.createRef();

    this.state = {
      ...this.props.self,
      open: false,
      expandVisible: false,
      deletable: false,
      bottomBorder: false,
    };

    console.log("bullet state", this.state);
  }

  onMouseEnter() {
    this.setState({
      expandVisible: true
    });
  }

  onMouseLeave() {
    this.setState({
      expandVisible: false
    });
  }

  onKeyDown(e) {
    switch (e.keyCode) {
      case 9: // tab
        if (e.shiftKey) { // shift + tab
          this.props.store.dispatch(Actions.unindentBullet([0, ...this.props.position]));
          e.preventDefault();
          break;
        }
        else { // tab
          this.props.store.dispatch(Actions.indentBullet([0, ...this.props.position]));
          e.preventDefault();
          break;
        }
      case 13: // enter
        let newUUID = uuidv1();
        this.props.store.dispatch(Actions.addBullet([0, ...this.props.position], newUUID));
        e.preventDefault();
        break;
      default:
        if (this.content.current.innerText == "") {
          this.setState({ deletable: true });
        }
        break;
    }
  }

  onKeyUp(e) {
    switch (e.keyCode) {
      case 8: // backspace
        if (this.state.deletable) {
          this.props.store.dispatch(Actions.deleteBullet([0, ...this.props.position]));
          e.preventDefault();
          break;
        }
      default:
        this.props.store.dispatch(Actions.editBullet([0, ...this.props.position], this.content.current.innerText));
        this.setState({ deletable: false });
        break;
    }
  }

  onDragStart(e) {
    e.dataTransfer.setData("Text", [0, ...this.props.position].toString());
  }

  onDragEnter(e) {
    this.setState({ bottomBorder: true });
  }

  onDragOver(e) {
    e.preventDefault();
    this.setState({ bottomBorder: true });
  }

  onDragLeave(e) {
    this.setState({ bottomBorder: false });
  }

  onDropChild(e) {
    e.preventDefault();
    console.log("onDropChild");
    let childPosition = e.dataTransfer.getData("Text").split(",").map(index => parseInt(index));
    let newParentPosition = [0, ...this.props.position];
    this.props.store.dispatch(Actions.moveBulletAsChild(childPosition, newParentPosition));
    this.setState({ bottomBorder: false });
  }

  onDropSibling(e) {
    e.preventDefault();
    console.log("onDropSibling");
    let childPosition = e.dataTransfer.getData("Text").split(",").map(index => parseInt(index));
    let newSiblingPosition = [0, ...this.props.position];
    this.props.store.dispatch(Actions.moveBulletAsSibling(childPosition, newSiblingPosition));
    this.setState({ bottomBorder: false });
  }

  componentDidMount() {
    if ($(`#${this.state.id}`).find(".children").attr("class").indexOf("show")  != -1) {
      this.setState({
        open: true
      });
    }
  }

  render() {
    return (
      <div
           className="bullet ml-5"
           id={ this.state.id }
           onMouseEnter={ this.onMouseEnter.bind(this) }
           onMouseLeave={ this.onMouseLeave.bind(this) }>
        <ul id="content" className={ "content position-relative list-inline mb-1" + (this.state.bottomBorder ? " border-bottom" : "")}>
          <li draggable={ true }
              className="list-inline-item"
              onDragStart={ this.onDragStart.bind(this) }
              onDragEnter={ this.onDragEnter.bind(this) }
              onDragOver={ this.onDragOver.bind(this) }
              onDragLeave={ this.onDragLeave.bind(this) }
              onDrop={ this.onDropSibling.bind(this) }>
            <a className="icon">
              <svg height="35px" width="35px" viewBox="0 0 100 100">
                <circle cx="50%" cy="50%" r="25" stroke="lightgrey" strokeWidth="25" fill="dimgrey" />
              </svg>
            </a>
          </li>
          <li className="list-inline-item align-text-top w-75"
              onDrop={ this.onDropChild.bind(this) }>
            <div className="bullet-content" contentEditable="true" suppressContentEditableWarning="true" ref={ this.content } style={{ outline: "0px solid transparent" }} onKeyDown={ this.onKeyDown.bind(this) } onKeyUp={ this.onKeyUp.bind(this) } onDragEnter={ e => e.preventDefault() } onDragOver={ e => e.preventDefault() }>
              { this.state.content }
            </div>
          </li>
          <li className="list-inline-item align-middle position-absolute" style={{ left: -20, top: 3 }}>
            <a className="expand border-0" onClick={() => this.setState({ open: !this.state.open })} style={{ backgroundColor: "transparent" }}>
              <svg height="10px" width="10px" viewBox="0 0 100 100">
                { this.state.children.length > 0 && this.state.expandVisible && !this.state.open &&
                    <polygon points="0,0 0,100 100,50" fill="dimgrey"/>
                }
                { this.state.children.length > 0 && this.state.expandVisible && this.state.open &&
                    <polygon points="0,0 50,100 100,0" fill="dimgrey"/>
                }
              </svg>
            </a>
          </li>
        </ul>
        <div className="note" style={{ display: "none" }}>
          <div className="bullet-note" contentEditable="true" suppressContentEditableWarning="true">
            { this.state.note }
          </div>
        </div>
        <Collapse in={ this.state.open } timeout={10}>
          <div className="children border-left ml-3">
            { this.state.children.length > 0 &&
                this.state.children.map((child, index) =>
                  <Bullet key={ child.id } position={ [...this.props.position, index] } store={ this.props.store } self={ child }></Bullet>
                )
            }
          </div>
        </Collapse>
      </div>
    )
  }
}

export default Bullet;
