import React, { Component } from "react";

class View extends Component {

  constructor(props) {
    super(props);

    this.state = {
      content: this.props.content,
      note: this.props.note,
      completed: this.props.completed,
      children: this.props.children,
      mounted: false
    }
  }

  componentDidMount() {
    this.setState({
      mounted: true
    });
  }

  updateState(props) {
    this.setState({
      content: this.props.content,
      note: this.props.note,
      completed: this.props.completed,
      children: this.props.children
    })
  }

  render() {
    return (
      <div className="bullet collapsed">
        <div className="name">
          <a className="icon">
            <svg height="40px" width="40px" viewBox="0 0 100 100">
              <circle cx="50%" cy="50%" r="25" stroke="lightgrey" stroke-width="25" fill="dimgrey" />
            </svg>
          </a>
          <div className="content" contenteditable="true">
            { this.state.content }
          </div>
          <a className="expand">
            <svg height="20px" width="20px" viewBox="0 0 100 100">
              <polygon points="0,0 0,100 100,50" fill="dimgrey"/>
            </svg>
          </a>
        </div>
        <div className="note">
          <div className="content" contenteditable="true">
            { this.state.note }
          </div>
        </div>
        <div className="children">
          { this.state.children.map()}
        </div>
      </div>
    )
  }
}

export default View;
