import React, { Component } from 'react';
import Bullet from "./Bullet.js";
import './App.css';
import { createStore } from "redux";
import yaml from "yaml-js";
import Reducer from "./Reducer.js";
import * as Actions from "./Actions.js";

let content = `
- content: ""
  note: ""
  completed: false
  children:
  - content: "Note1"
    note: ""
    completed: false
    children: []
  - content: "Note2"
    note: "Note2 Note"
    completed: true
    children:
      - content: "Note2a"
        note: ""
        completed: false
        children:
        - content: "Note2a!"
          note: ""
          completed: false
          children: []
        - content: "Note2a@"
          note: ""
          completed: false
          children: []
      - content: "Note2b"
        note: ""
        completed: false
        children: []
`;

let all = yaml.load(content);

let root = [];


class App extends Component {

  constructor(props) {
    super(props);
    this.store = createStore(Reducer, { all, root });
    this.state = this.store.getState();
  }

  componentDidMount() {
    this.unsubscribe = this.store.subscribe(() => {
      this.setState(this.store.getState());
      console.log("subscription state", this.state);
      this.render();
    });
  }

  render() {
    const { root } = this.state;
    const { all } = this.state;
    const rootBullet = Actions.getNote([0, ...root], all);
    return (
      <div className="container">
        <h1 className="display-1">{ rootBullet.content }</h1>
        <h1 className="display-5">{ rootBullet.note }</h1>
        <hr></hr>
        { rootBullet.children.length > 0 &&
            rootBullet.children.map((child, index) =>
              <Bullet key={ index } position={ [...root, index] } store={ this.store } note={ child }></Bullet>
            )
        }
      </div>
    );
  }
}

export default App;
