import React, { Component } from "react";

class Bullet extends Component {

  constructor(props) {
    super(props);

    console.log(this.props.note);

    this.state = {
      ...this.props.note
    };
    console.log(this.state);
  }

  componentDidMount() {
  }

  render() {
    return (
      <div className="bullet ml-5">
        <ul className="name list-inline">
          <li className="list-inline-item">
            <a className="expand">
              <svg height="20px" width="20px" viewBox="0 0 100 100">
                <polygon points="0,0 0,100 100,50" fill="dimgrey"/>
              </svg>
            </a>
          </li>
          <li className="list-inline-item">
            <a className="icon">
              <svg height="40px" width="40px" viewBox="0 0 100 100">
                <circle cx="50%" cy="50%" r="25" stroke="lightgrey" stroke-width="25" fill="dimgrey" />
              </svg>
            </a>
          </li>
          <li className="list-inline-item">
            <div className="content" contenteditable="true">
              { this.state.content }
            </div>
          </li>
        </ul>
        <div className="note">
          <div className="content" contenteditable="true">
            { this.state.note }
          </div>
        </div>
        { this.state.children.length > 0 &&
          <div className="children">
            { this.state.children.map((child) => <Bullet bullet={ child }/>)}
          </div>
        }
      </div>
    )
  }
}

export default Bullet;
