import React, { Component } from "react";

class Bullet extends Component {

  constructor(props) {
    super(props);

    this.state = {
      model: this.props.model
    }
  }

  componentDidMount() {
    this.setState({
      mounted: true
    });
  }

  updateState() {
    if (this.state.mounted) {
      this.setState({
        model: this.props.model
      })
    };
  }

  render() {
    return (
      <div className="bullet collapsed">
        <div className="name">
          <div className="container">
            <div className="row">
              <a className="expand col-sm-1 p-0 m-0">
                <svg height="20px" width="20px" viewBox="0 0 100 100">
                  <polygon points="0,0 0,100 100,50" fill="dimgrey"/>
                </svg>
              </a>
              <a className="icon col-sm-1">
                <svg height="40px" width="40px" viewBox="0 0 100 100">
                  <circle cx="50%" cy="50%" r="25" stroke="lightgrey" stroke-width="25" fill="dimgrey" />
                </svg>
              </a>
              <div className="content col-sm-10" contenteditable="true">
                { this.state.model.content }
              </div>
            </div>
          </div>
        </div>
        { false &&
          <div className="note">
            <div className="content" contenteditable="true">
              { this.state.model.note }
            </div>
          </div>
        }
        <div className="children">
          { this.state.model.children.map((child) => <Bullet model={ child }/>)}
        </div>
      </div>
    )
  }
}

export default Bullet;
