import React, { Component } from "react";
import { Collapse } from "react-bootstrap";
import * as Actions from "./Actions.js";
import uuidv1 from "uuid/v1";

class Bullet extends Component {

  constructor(props) {
    super(props);

    this.content = React.createRef();

    this.state = {
      ...this.props.self,
      open: false,
      expandVisible: false,
      keyMap: {
        8: false, // backspace
        9: false, // tab
        13: false, // enter
        15: false // shift
      }
    };

    console.log("bullet state", this.state);
  }

  onMouseEnter() {
    this.setState({
      expandVisible: true
    });
  }

  onMouseLeave() {
    this.setState({
      expandVisible: false
    });
  }

/*

  keyPress(e) {
    if (this.state.keyMap[13]) {
      let newUUID = uuidv1();
      this.props.store.dispatch(Actions.addBullet([0, ...this.props.position], newUUID));
      e.preventDefault();
    }
    else if (this.state.keyMap[9] && e.shiftKey) {
      this.props.store.dispatch(Actions.unindentBullet([0, ...this.props.position]));
      e.preventDefault();
    }
    else if (this.state.keyMap[9] && !e.shiftKey) {
      this.props.store.dispatch(Actions.indentBullet([0, ...this.props.position]));
      e.preventDefault();
    }
    else if (this.state.keyMap[8] && !(this.state.content == "")) {
      this.props.store.dispatch(Actions.editBullet([0, ...this.props.position], "\b"))
      e.preventDefault();
    }
    else if (this.state.keyMap[8] && (this.state.content == "")) {
      console.log("deleting");
      this.props.store.dispatch(Actions.deleteBullet([0, ...this.props.position]));
      e.preventDefault();
    }
  }

  onKeyDown(e) {
    let { keyMap } = this.state;
    console.log(e.keyCode);
    keyMap[e.keyCode] = true;
    this.setState({ keyMap });
    //console.log("onKeyDown state", this.state);
    this.keyPress(e);
  }

  onKeyUp(e) {
    let { keyMap } = this.state;
    if ((91 > e.keyCode && e.keyCode > 47) || (["^", "!", "#", "$"].indexOf(e.keyCode) != -1)) {
      this.props.store.dispatch(Actions.editBullet([0, ...this.props.position], this.content.current.innerText));
    }
    keyMap[e.keyCode] = false;
    this.setState({ keyMap });
    console.log("onKeyUp state", this.state);
  }

*/

  onKeyDown(e) {
    switch (e.keyCode) {
      case 8:
        if (this.content.current.innerText == "") {
          console.log("deleting");
          this.props.store.dispatch(Actions.deleteBullet([0, ...this.props.position]));
          break;
        }
      case 9:
        if (e.shiftKey) {
          this.props.store.dispatch(Actions.unindentBullet([0, ...this.props.position]));
          e.preventDefault();
          break;
        }
        else {
          this.props.store.dispatch(Actions.indentBullet([0, ...this.props.position]));
          e.preventDefault();
          break;
        }
      case 13:
        let newUUID = uuidv1();
        this.props.store.dispatch(Actions.addBullet([0, ...this.props.position], newUUID));
        e.preventDefault();
        break;
      default:
        this.props.store(dispatch(Actions.editBullet([0, ...this.props.position], this.content.current.innerText)))
    }
  }

  onKeyUp(e) {
  }

  componentDidMount() {
    this.unsubscribe = this.props.store.subscribe(() => {
      this.setState({
        ...Actions.getNode([0, ...this.props.position], this.props.store.getState().root)
      });
      console.log("bullet store subscription state", this.state);
    })
  }

  render() {
    return (
      <div className="bullet ml-5" id={ this.state.id } onMouseEnter={ this.onMouseEnter.bind(this) } onMouseLeave={ this.onMouseLeave.bind(this) }>
        <ul id="content" className="content list-inline mb-1">
          <div className="list-inline-item align-top">
            <li className="list-inline-item">
              <button className="expand border-0" onClick={() => this.setState({ open: !this.state.open })} style={{ backgroundColor: "transparent" }}>
                <svg height="10px" width="10px" viewBox="0 0 100 100">
                  { this.state.children.length > 0 && this.state.expandVisible && !this.state.open &&
                      <polygon points="0,0 0,100 100,50" fill="dimgrey"/>
                  }
                  { this.state.children.length > 0 && this.state.expandVisible && this.state.open &&
                      <polygon points="0,0 50,100 100,0" fill="dimgrey"/>
                  }
                </svg>
              </button>
            </li>
            <li className="list-inline-item">
              <a className="icon">
                <svg height="35px" width="35px" viewBox="0 0 100 100">
                  <circle cx="50%" cy="50%" r="25" stroke="lightgrey" strokeWidth="25" fill="dimgrey" />
                </svg>
              </a>
            </li>
          </div>
          <li className="list-inline-item align-text-top w-75">
            <div className="bullet-content" contentEditable="true" suppressContentEditableWarning="true" ref={ this.content } style={{ outline: "0px solid transparent" }} onKeyDown={ this.onKeyDown.bind(this) } onKeyUp={ this.onKeyUp.bind(this) }>
              { this.state.content }
            </div>
          </li>
        </ul>
        <div className="note" style={{ display: "none" }}>
          <div className="bullet-note" contentEditable="true" suppressContentEditableWarning="true">
            { this.state.note }
          </div>
        </div>
        <Collapse in={ this.state.open } timeout={10}>
          <div className="children">
            { this.state.children.length > 0 &&
                this.state.children.map((child, index) =>
                  <Bullet key={ child.id } position={ [...this.props.position, index] } store={ this.props.store } self={ child }></Bullet>
                )
            }
          </div>
        </Collapse>
      </div>
    )
  }
}

export default Bullet;
