import React, { Component } from "react";
import { Collapse } from "react-bootstrap";
import * as Actions from "./Actions.js";
import uuidv1 from "uuid/v1";

class Bullet extends Component {

  constructor(props) {
    super(props);

    this.input = React.createRef();

    this.state = {
      ...this.props.note,
      open: false,
      expandVisible: false,
      keyMap: {
        9: false, // tab
        13: false, // enter
      }
    };
  }

  onMouseEnter() {
    this.setState({
      expandVisible: true
    });
  }

  onMouseLeave() {
    this.setState({
      expandVisible: false
    });
  }

  keyPress(e) {
    if (this.state.keyMap[13]) {
      let newUUID = uuidv1();
      this.props.store.dispatch(Actions.addBullet([0, ...this.props.position], newUUID));
      e.preventDefault();
      setTimeout(() => {
        document.getElementById(newUUID).focus();
      }, 1000);
    }
  }

  onKeyDown(e) {
    let { keyMap } = this.state;
    keyMap[e.keyCode] = true;
    this.setState({ keyMap });
    this.keyPress(e);
  }

  onKeyUp(e) {
    let { keyMap } = this.state;
    keyMap[e.keyCode] = false;
    this.setState({ keyMap });
  }

  componentDidMount() {
  }

  render() {
    return (
      <div className="bullet ml-5" id={ this.props.id } onMouseEnter={ this.onMouseEnter.bind(this) } onMouseLeave={ this.onMouseLeave.bind(this) }>
        <ul className="list-inline mb-1">
          <div className="list-inline-item align-top">
            <li className="list-inline-item">
              <button className="expand border-0" onClick={() => this.setState({ open: !this.state.open })} style={{ backgroundColor: "transparent" }}>
                <svg height="10px" width="10px" viewBox="0 0 100 100">
                  { this.state.children.length > 0 && this.state.expandVisible && !this.state.open &&
                      <polygon points="0,0 0,100 100,50" fill="dimgrey"/>
                  }
                  { this.state.children.length > 0 && this.state.expandVisible && this.state.open &&
                      <polygon points="0,0 50,100 100,0" fill="dimgrey"/>
                  }
                </svg>
              </button>
            </li>
            <li className="list-inline-item">
              <a className="icon">
                <svg height="35px" width="35px" viewBox="0 0 100 100">
                  <circle cx="50%" cy="50%" r="25" stroke="lightgrey" strokeWidth="25" fill="dimgrey" />
                </svg>
              </a>
            </li>
          </div>
          <li className="list-inline-item align-text-top w-75">
            <div className="content" contentEditable="true" suppressContentEditableWarning="true" style={{ outline: "0px solid transparent" }} onKeyDown={ this.onKeyDown.bind(this) } onKeyUp={ this.onKeyUp.bind(this) }>
              { this.state.content }
            </div>
          </li>
        </ul>
        <div className="note" style={{ display: "none" }}>
          <div className="content" contentEditable="true" suppressContentEditableWarning="true">
            { this.state.note }
          </div>
        </div>
        <Collapse in={ this.state.open } timeout={10}>
          <div className="children">
            { this.state.children.length > 0 &&
                this.state.children.map((child, index) =>
                  <Bullet key={ child.id } id={ child.id } position={ [...this.props.position, index] } store={ this.props.store } note={ child }></Bullet>
                )
            }
          </div>
        </Collapse>
      </div>
    )
  }
}

export default Bullet;
