import React, { Component } from 'react';
import Page from "./Page.js";
import Bullet from "./Bullet.js";
import Note from "./Note.js";
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      notes: [
        {
          content: "",
          note: "",
          completed: false,
          children: []
        }
      ],
      currentNotePosition: [0,]
    }
  }

  componentDidMount() {
    const { notes, currentNotePosition } = this.props.store.getState();
    this.setState({ notes, currentNotePosition });
    this.unsubscribe = this.props.store.subscribe(() => {
      const { notes, currentNotePosition } = this.props.store.getState();
      this.setState({ notes, currentNotePosition });
    });
  }

  render() {
    let currentNote = this.state.notes[this.state.currentNotePosition[0]];
    if (this.state.currentNotePositon > 1) {
      let index;
      for (index in this.state.currentNotePosition.slice(1, this.state.currentNotePosition.length)) {
        currentNote = currentNote[index];
      }
    }
    return (
      <Page currentNote={ currentNote }></Page>
    );
  }
}

export default App;
