import React, { Component } from 'react';
import Bullet from "./Bullet.js";
import './App.css';
import { createStore } from "redux";
import yaml from "yaml-js";
import Reducer from "./Reducer.js";
import * as Actions from "./Actions.js";

let content = `
- content: ""
  note: ""
  completed: false
  children:
  - content: "Note1"
    note: ""
    completed: false
    children: []
  - content: "Note2"
    note: "Note2 Note"
    completed: true
    children:
      - content: "Note2a"
        note: ""
        completed: false
        children:
        - content: "Note2a!"
          note: ""
          completed: false
          children: []
        - content: "Note2a@"
          note: ""
          completed: false
          children: []
      - content: "Note2b"
        note: ""
        completed: false
        children: []
`;

let all = yaml.load(content);

let rootBullet = [0,];


class App extends Component {

  constructor(props) {
    super(props);
    this.store = createStore(Reducer, { all, rootBullet });
    this.state = {
      all: this.store.getState().all,
      rootBullet: this.store.getState().rootBullet
    }
  }

  componentDidMount() {
    this.unsubscribe = this.store.subscribe(() => {
      this.setState({
        all: this.store.getState().all,
        rootBullet: this.store.getState().rootBullet
      });
    });    
  }

  render() {
    let { rootBullet } = this.state;
