    console.log(currentNote);
    return (
      <div className="container">
        <h1 className="display-1">{ currentNote.content }</h1>
        <h1 className="display-5">{ currentNote.note }</h1>
        <hr></hr>
        { currentNote.children.length > 0 &&
            currentNote.children.map((child, index) =>
              <Bullet position={ [...this.state.currentNotePosition, index] } store={ this.store } note={ child }></Bullet>
            )
        }
      </div>
    );
  }
}

export default App;
