import React, { Component } from "react";
import { Collapse } from "react-bootstrap";
import * as Actions from "./Actions.js";
import $ from "jquery";

class Bullet extends Component {

  constructor(props) {
    super(props);

    this.state = {
      ...this.props.note,
      open: false,
      expandDisplay: false,
      keyMap: {
        9: false, // tab
        13: false, // enter
      }
    };
  }

  onMouseEnter() {
    this.setState({
      expandDisplay: true
    });
  }

  onMouseLeave() {
    this.setState({
      expandDisplay: false
    });
  }

  componentDidMount() {
  }

  render() {
    return (
      <div className="bullet ml-5" onMouseEnter={ this.onMouseEnter.bind(this) } onMouseLeave={ this.onMouseLeave.bind(this) }>
        <ul className="list-inline mb-1">
          <li className="list-inline-item align-top">
            <li className="list-inline-item">
              <a className="expand" onClick={() => this.setState({ open: !this.state.open })}>
                <svg height="10px" width="10px" viewBox="0 0 100 100">
                  { this.state.children.length > 0 && this.state.expandDisplay && !this.state.open &&
                      <polygon points="0,0 0,100 100,50" fill="dimgrey"/>
                  }
                  { this.state.children.length > 0 && this.state.expandDisplay && this.state.open &&
                      <polygon points="0,0 50,100 100,0" fill="dimgrey"/>
                  }
                </svg>
              </a>
            </li>
            <li className="list-inline-item">
              <a className="icon">
                <svg height="35px" width="35px" viewBox="0 0 100 100">
                  <circle cx="50%" cy="50%" r="25" stroke="lightgrey" strokeWidth="25" fill="dimgrey" />
                </svg>
              </a>
            </li>
          </li>
          <li className="list-inline-item align-text-top w-75">
            <div className="content" contentEditable="true" style={{ outline: "0px solid transparent" }}>
              { this.state.content }
            </div>
          </li>
        </ul>
        <div className="note" style={{ display: "none" }}>
          <div className="content" contenteditable="true">
            { this.state.note }
          </div>
        </div>
        <Collapse in={ this.state.open } timeout={10}>
          <div className="children">
            { this.state.children.length > 0 &&
                this.state.children.map((child, index) =>
                  <Bullet position={ [...this.props.position, index] } store={ this.props.store } note={ child }></Bullet>
                )
            }
          </div>
        </Collapse>
      </div>
    )
  }
}

export default Bullet;
