import $ from "jquery";

const ADD_BULLET = "ADD_BULLET";
const EDIT_BULLET = "EDIT_BULLET";
const REMOVE_BULLET = "REMOVE_BULLET";
const INDENT_BULLET = "INDENT_BULLET";
const UNINDENT_BULLET = "UNINDENT_BULLET";
const CHANGE_CURRENT_NOTE = "CHANGE_CURRENT_NOTE";

function addBullet(position, uuid) {
  return {
    type: ADD_BULLET,
    position,
    uuid,
    exec: (state) => {
      let childPosition = position.pop();
      let parent = state.root[position.shift()].children;
      while (position.length > 0) {
        parent = state.root[position.shift()].children;
      }
      parent.splice(childPosition + 1, 0, {content: "", note: "", id: uuid, completed: false, children: []});
      setTimeout(() => {
        document.getElementById(uuid).focus();
      }, 0);
      return state;
    }
  }
}

function editBullet(position, content) {
  return {
    type: EDIT_BULLET,
    position,
    content
  }
}

function removeBullet(position) {
  return {
    type: REMOVE_BULLET,
    position
  }
}

function indentBullet(position) {
  return {
    type: INDENT_BULLET,
    position,
    exec: (state) => {
      let childPosition = position.pop();
      if (childPosition == 0) { return state }
      let parents = getTree(position, state.root)
      let child = parent.children.splice(childPosition, 1)[0];
      let newParent = parent.children[childPosition-1];
      newParent.children.push(child);
      parents.push(newParent);
      setTimeout(() => {
        for (let x = 0; x < parents.length; x++) {
          let selector = `#${ parents[x].id }`;
          console.log(selector);
          $(selector).closest(".bullet").children(".collapse").addClass("show");
        }
      }, 0);
      return state;
    }
  }
}

function unindentBullet(position) {
  return {
    type: UNINDENT_BULLET,
    position,
    exec: (state) => {
      let childPosition = position.pop();
      if (childPosition == 0) { return state }
      let parents = [];
      let parentPosition = position.shift();
      let parent = state.root[parentPosition];
      parents.push(parent);
      while (position.length > 0) {
        parentPosition = position.shift();
        parent = parent.children[parentPosition];
        parents.push(parent);
      }
      let child = parent.children.splice(childPosition, 1)[0];
      let above = parent.children[childPosition-1];
      above.children.push(child);
      parents.push(above);
      setTimeout(() => {
        for (let x = 0; x < parents.length; x++) {
          let selector = `#${ parents[x].id }`;
          console.log(selector);
          $(selector).closest(".bullet").find(".collapse").addClass("show");
        }
      }, 0);
      return state;
    }
  }
}

function getNode(indices, root) {
  let node = root[indices.shift()];
  while (indices.length > 0) {
    node = node.children[indices.shift()];
  }
  return node;
}

function getTree(indices, root) {
  let tree = [];
  let node = root[indices.shift()];
  tree.push(node)
  while (indices.length > 0) {
    node = node.children[indices.shift()];
    tree.push(node);
  }
  return tree;
}



export { ADD_BULLET,
         EDIT_BULLET,
         REMOVE_BULLET,
         INDENT_BULLET,
         UNINDENT_BULLET,
         addBullet,
         editBullet,
         removeBullet,
         indentBullet,
         unindentBullet,
         getNode,
         getTree };
